import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Card from './components/Card';

function App() {
  const [cards, setCards] = useState([]);
  const [displayCount, setDisplayCount] = useState(10);
  const [selectedCard, setSelectedCard] = useState(null);

  useEffect(() => {
    const savedCards = localStorage.getItem('cards');
    if (savedCards) {
      setCards(JSON.parse(savedCards));
    } else {
      fetch('https://rickandmortyapi.com/api/character')
        .then(response => response.json())
        .then(data => {
          const formattedCards = data.results.map(card => ({
            id: card.id,
            name: card.name,
            image: card.image,
            status: card.status,
            species: card.species,
            gender: card.gender,
            isFavorite: false
          }));
          setCards(formattedCards);
          localStorage.setItem('cards', JSON.stringify(formattedCards));
        })
        .catch(error => console.log('Error fetching data: ', error));
    }
  }, []);

  const onToggleFavorite = (id) => {
    const updatedCards = cards.map(card => {
      if (card.id === id) {
        return { ...card, isFavorite: !card.isFavorite };
      }
      return card;
    });
    setCards(updatedCards);
    localStorage.setItem('cards', JSON.stringify(updatedCards));
  };

  const showMoreCards = () => {
    setDisplayCount(prevCount => prevCount + 10);
  };

  const handleCardClick = (card) => {
    setSelectedCard(card);
  };

  return (
    <div className="wrapper">
      <Header />
      <main>
        <section className="container">
          <div className="wrapper">
            {cards.slice(0, displayCount).map(card => (
              <Card
                key={card.id}
                card={card}
                onToggleFavorite={onToggleFavorite}
                onCardClick={handleCardClick}
              />
            ))}
            {displayCount < cards.length && (
              <div className='moreButton'>
                <button onClick={showMoreCards}>Показать еще</button>
              </div>
            )}
          </div>
          {selectedCard && (
            <div className="info-box">
              <h2 className="info-name">{selectedCard.name}</h2>
              <p className="info-text">
                Status: {selectedCard.status}, Species: {selectedCard.species}, Gender: {selectedCard.gender}
              </p>
            </div>
          )}
        </section>
      </main>
      <Footer/>
    </div>
  );
}

export default App;
