import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Main from './components/Cards';

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      cards: [
        {
          id: 1,
          title: 'ЧМ))))',
          img: 'ЧМ.jpg',
          desc: 'Лучший курс из всех'
        },
        {
          id: 2,
          title: 'ТПР',
          img: 'ТПР.jpg',
          desc: 'Теория принятия решений'
        },
        {
          id: 3,
          title: 'САОД',
          img: 'eV1Z14HceNQ.jpg',
          desc: 'Структуры и алгоритмы обработки данных'
        },
        {
          id: 4,
          title: 'ТБД',
          img: 'БД.jpg',
          desc: 'Технологии баз данных'
        }
      ]
    }
  }
  render(){
    return(
      <div className="wrapper">
        <Header />
        <main>
            <section className="container">
                <div className="wrapper">
                    <div className="cards">
                        <Cards cards={this.state.items} />
                    </div>
                </div>
            </section>
        </main>
        <Footer />
      </div>
    )
  }
}

export default App;
