import React, { Component } from 'react'

export class Card extends Component {
    toggleFavorite = () => {
        this.props.onToggleFavorite(this.props.card.id);
    }

    render() {
        const { card, isFavorite } = this.props;
        return (
            <div className={`card ${isFavorite ? 'favorite' : ''}`}>
                <div className="card_image">
                    <img src={"./images/" + card.img} alt={card.title}></img>
                </div>
                <div className="card_content">
                    <div className="card_texts">
                        <h2>{card.title}</h2>
                    </div>
                    <div className='add-to-favorite' onClick={this.toggleFavorite}>
                        {isFavorite ? "Убрать из любимого" : "Любимое"}
                    </div>
                </div>
            </div>
        )
    }
}

export default Card
