import React, { useState } from 'react';

function Card({ card, onToggleFavorite, onCardClick }) {
  const [isFavorite, setIsFavorite] = useState(card.isFavorite);

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
    onToggleFavorite(card.id);
  };

  const toggleInfo = () => {
    setIsInfoShown(!isInfoShown)
  }

  return (
    <div className={`card ${isFavorite ? 'favorite' : ''}`}>
      <div className="card_image" onClick={toggleInfo}>
        <img src={card.image} alt={card.name}></img>
      </div>
      <div className="card_content">
        <div className="card_texts">
          <h2>{card.name}</h2>
        </div>
        <div className='add-to-favorite' onClick={toggleFavorite}>
          {isFavorite ? "Убрать" : "Добавить"}
        </div>
      </div>
    </div>
  );
}

export default Card;
