import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Cards from './components/Cards';

function App() {
  const [cards, setCards] = useState(() => {
    const savedCards = localStorage.getItem('cards');
    if (savedCards) {
      return JSON.parse(savedCards);
    }

    return [
      { id: 1, title: 'Разработка веб-приложений', img: 'svg/load.svg', isFavorite: false },
      { id: 2, title: 'Структуры и алгоритмы обработки данных. Часть 2.', img: 'svg/load.svg', isFavorite: false },
      { id: 3, title: 'Теория принятия решений (очная форма)', img: 'svg/load.svg', isFavorite: false }
    ];
  });


  useEffect(() => {
    localStorage.setItem('cards', JSON.stringify(cards));
  }, [cards]); // Эффект выполняется при изменении cards

  const onToggleFavorite = (id) => {
    setCards(prevCards =>
      prevCards.map(card => {
        if (card.id === id) {
          return { ...card, isFavorite: !card.isFavorite };
        }
        return card;
      })
    );
  };

  return (
    <div className="wrapper">
      <Header />
      <main>
        <section className="container">
          <div className="wrapper">
            <Cards cards={cards} onToggleFavorite={onToggleFavorite} />
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

export default App;
