import React, { Component } from 'react'

export class Card extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isFavorite: false // начальное состояние, не в избранном
        };
    }

    toggleFavorite = () => {
        this.setState({ isFavorite: !this.state.isFavorite });
    }

    render() {
        const { card } = this.props;
        const { isFavorite } = this.state;
        return (
            <div className={`card ${isFavorite ? 'favorite' : ''}`}>
                <img src={"./img/" + card.img} alt={card.title}></img>
                <h2>{card.title}</h2>
                <p>{card.desc}</p>
                <div className='add-to-favorite' onClick={this.toggleFavorite}>
                    {isFavorite ? "Убрать из любимого" : "Любимое"}
                </div>
            </div>
        )
    }
}

export default Card
