import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Cards from './components/Cards';

class App extends React.Component {
  constructor(props){
    super(props);
    const initialCards = [
      { id: 1, title: 'Разработка веб-приложений', img: 'svg/load.svg', isFavorite: false },
      { id: 2, title: 'Структуры и алгоритмы обработки данных. Часть 2.', img: 'svg/load.svg', isFavorite: false },
      { id: 3, title: 'Теория принятия решений (очная форма)', img: 'svg/load.svg', isFavorite: false }
    ];
    this.state = {
      cards: JSON.parse(localStorage.getItem('cards')) || initialCards
    };
  }

  onToggleFavorite = (id) => {
    const updatedCards = this.state.cards.map(card => {
      if (card.id === id) {
        return { ...card, isFavorite: !card.isFavorite };
      }
      return card;
    });
    this.setState({ cards: updatedCards });
  }

  componentDidUpdate() {
    localStorage.setItem('cards', JSON.stringify(this.state.cards));
  }

  render(){
    return(
      <div className="wrapper">
        <Header />
        <main>
            <section className="container">
                <div className="wrapper">
                    <Cards cards={this.state.cards} onToggleFavorite={this.onToggleFavorite} />
                </div>
            </section>
        </main>
        <Footer />
      </div>
    )
  }
}

export default App;
